BEGIN ;
CREATE TABLE `user` (`id` integer,`created_at` datetime,`updated_at` datetime,`deleted_at` datetime,`username` varchar(20) NOT NULL,`password` varchar(100) NOT NULL,`role` integer NOT NULL,`birthday` varchar(20),`email` varchar(20),`qq` varchar(20),`self_introduction` text, `self_intro` text, `selfintro` text,PRIMARY KEY (`id`));
CREATE TABLE `category` (`id` integer,`created_at` datetime,`updated_at` datetime,`deleted_at` datetime,`name` varchar(20) NOT NULL,PRIMARY KEY (`id`));
CREATE TABLE `tag` (`id` integer,`created_at` datetime,`updated_at` datetime,`deleted_at` datetime,`name` varchar(20) NOT NULL,PRIMARY KEY (`id`));
CREATE TABLE `comment` (`id` integer,`created_at` datetime,`updated_at` datetime,`deleted_at` datetime,`user_name` varchar(20),`parent_id` integer,`contents` text,`article_id` integer,PRIMARY KEY (`id`));
CREATE TABLE IF NOT EXISTS `article_tag` (
	article_id INTEGER,
	tag_id INTEGER
);
CREATE TABLE IF NOT EXISTS `article` (`id` integer,`created_at` datetime,`updated_at` datetime,`deleted_at` datetime,`title` varchar(200) NOT NULL,`description` varchar(200) NOT NULL,`cate_id` integer NOT NULL,`author_id` integer NOT NULL,`upvotes` integer NOT NULL,`contents` text NOT NULL,`img` text NOT NULL,PRIMARY KEY (`id`),CONSTRAINT `fk_article_category` FOREIGN KEY (`cate_id`) REFERENCES `category`(`id`));
CREATE INDEX `idx_user_deleted_at` ON `user`(`deleted_at`);
CREATE INDEX `idx_category_deleted_at` ON `category`(`deleted_at`);
CREATE INDEX `idx_tag_deleted_at` ON `tag`(`deleted_at`);
CREATE INDEX `idx_comment_deleted_at` ON `comment`(`deleted_at`);
CREATE INDEX `idx_article_deleted_at` ON `article`(`deleted_at`);
COMMIT;
